lass('current').parent().next().children().eq(index).show().siblings().hide();    })     function order(category,id,uid){         var url = "<?php echo Url::toRoute('/capital/collectionlist')?>";         $.post(url,{category:category,id:id,uid:uid},function(v){               if(v.status == 0){                   $.msgbox({                       height: 130,                       width: 300,                       content: '<p style="text-align:center">您还未登录,请先<a href="<?php echo Url::toRoute('/site/login')?>" style="color:#FF9700">登录</a></p>',                       type: 'confirm',                       onClose: function (v) {                           if (v) {                               location.href = "<?php echo Url::toRoute('/site/login')?>";                           } else {                           }                       }                   });               }else if (v.status == 1){                   $.msgbox({                       height: 130,                       width: 300,                       content: '<p style="text-align:center">请不要收藏自己发布的数据。</p>',                       type: 'confirmyes',                       onClose: function (v) {                       }                   });               }else if(v.status == 2){                   $.msgbox({                       height: 130,                       width: 300,                       content: '<p style="text-align:center">请不要重复收藏。</p>',                       type: 'confirmyes',                       onClose: function (v) {                       }                   });               }else if(v.status == 3){                   $.msgbox({                       height: 130,                       width: 300,                       content: '<p style="text-align:center">个人用户只能发布数据</p>',                       type: 'confirmyes',                       onClose: function (v) {                       }                   });               }else if(v.status == 4){                   $.msgbox({                       height: 130,                       width: 300,                       content: '<p style="text-align:center">律所账号只能代理诉讼、清收</p>',                       type: 'confirmyes',                       onClose: function (v) {                       }                   });               }else if(v.status == 5){                   $.msgbox({                       height: 130,                       width: 300,                       content: '<p style="text-align:center">公司账号只能代理融资、清收</p>',                       type: 'confirmyes',                       onClose: function (v) {                       }                   });               }else{                   $.msgbox({                       height:120,                       width: 300,                       content: '<p style="text-align:center">收藏成功</p>',                       type: 'confirmyes',                       onClose: function (v) {                       }                   });               }         },'json')     }     function applys(category,id,uid){         var url = "<?php echo Url::toRoute('/capital/applysuccessful')?>";         $.post(url,{category:category,id:id,uid:uid},function(i){              if(i.status == 0){                  $.msgbox({                      height: 130,                      width: 300,                      content: '<p style="text-align:center">您还未登录,请先<a href="<?php echo Url::toRoute('/site/login')?>" style="color:#FF9700">登录</a></p>',                      type: 'confirm',                      onClose: function (v) {                          if (v) {                              location.href = "<?php echo Url::toRoute('/site/login')?>";                          } else {                          }                      }                  });              }else if (i.status == 1){                  $.msgbox({                      height: 130,                      width: 300,                      content: '<p style="text-align:center">　请不要申请自己发布的数据。</p>',                      type: 'confirmyes',                      onClose: function (v) {                      }                  });              }else if(i.status == 2){                  $.msgbox({                      height: 130,                      width: 300,                      content: '<p style="text-align:center">请不要重复申请。</p>',                      type: 'confirmyes',                      onClose: function (v) {                          if(v){                              location.href="<?php echo Url::toRoute('/order/index')?>";                          }                      }                  });              }else if(i.status == 3){                  $.msgbox({                      height: 130,                      width: 300,                      content: '<p style="text-align:center">个人用户只能发布数据</p>',                      type: 'confirmyes',                      onClose: function (v) {                      }                  });              }else if(i.status == 4){                  $.msgbox({                      height: 130,                      width: 300,                      content: '<p style="text-align:center">律所账号只能代理诉讼、清收</p>',                      type: 'confirmyes',                      onClose: function (v) {                      }                  });              }else if(i.status == 5){                  $.msgbox({                      height: 130,                      width: 300,                      content: '<p style="text-align:center">公司账号只能代理融资、清收',                      type: 'confirmyes',                      onClose: function (v) {                          if(v){                              location.href="<?php echo Url::toRoute('/order/index')?>";                          }                      }                  });              }else if(i.status == 6){                  $.msgbox({                      height: 130,                      width: 300,                      content: '<p style="text-align:center">您还未认证请先去认证',                      type: 'confirm',                      onClose: function (v) {                          if(v){                              location.href="<?php echo Url::toRoute('/certification/index')?>";                          }                      }                  });              }else{                  $.msgbox({                      height: 120,                      width: 270,                      content: '<p style="text-align:center">是否申请?</p>',                      type: 'confirm',                      onClose: function (v) {                          if (v) {                              var url = "<?php echo Url::toRoute('/capital/read')?>";                              $.post(url, {category: category, id: id, uid: uid}, function (iv) {                                  if(iv == 1){                                      $('.rz_alert1').show();                                  }                              },'json');                          }                      }                  });              }         },'json');     }</script>